import { useState, useEffect } from 'react'
import Image from 'next/image'
import clsx from 'clsx'

import Pagination from 'components/pagination'

import HorizontalOrangeLine from 'assets/png/horizontal-orange-line.png'

import stl from './Testimonial.module.scss'

const Testimonial = () => {
  const [itemsPerPage, setItemsPerPage] = useState(2)
  const [currentPage, setCurrentPage] = useState(1)

  const updateItemsPerPage = () => {
    if (window.innerWidth < 848) setItemsPerPage(1)
    else setItemsPerPage(2)
  }

  useEffect(() => {
    updateItemsPerPage()
    window.addEventListener('resize', updateItemsPerPage)
    return () => window.removeEventListener('resize', updateItemsPerPage)
  }, [])

  const testimonials = [
    {
      id: 1,
      name: 'John Doe',
      title: 'SE. Software Engineer',
      avatar:
        'data:image/jpeg;base64,',
      content:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed at turpis eget elit dapibus sagittis.',
    },
    {
      id: 2,
      name: 'Jane Smith',
      title: 'UI/UX Designer',
      avatar:
        'data:image/jpeg;base64,',
      content:
        'Ut fringilla dolor ut augue aliquet, vitae tincidunt lorem condimentum. Curabitur non cursus mauris.',
    },
    {
      id: 3,
      name: '',
      title: 'Rebecca R.',
      avatar:
        'data:image/jpeg;base64,',
      content:
        'Ut fringilla dolor ut augue aliquet, vitae tincidunt lorem condimentum. Curabitur non cursus mauris.',
    },
    {
      id: 4,
      name: 'Kimberly S.',
      title: 'Lead Developer',
      avatar:
        'data:image/jpeg;base64,',
      content:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed at turpis eget elit dapibus sagittis.',
    },
    {
      id: 5,
      name: 'Andrew S.',
      title: 'Sales Expert',
      avatar:
        'data:image/jpeg;base64,',
      content:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed at turpis eget elit dapibus sagittis.',
    },
  ]

  const getCurrentPageItems = () => {
    const startIndex = (currentPage - 1) * itemsPerPage
    const endIndex = startIndex + itemsPerPage
    return testimonials.slice(startIndex, endIndex)
  }

  return (
    <section className={stl.section} id="testimonialSection">
      <div className={stl.heading}>
        <h3>
          <span>#</span>Testimonial
        </h3>
        <Image src={HorizontalOrangeLine} alt="horizontal-orange-line" />
      </div>

      <div className={stl.content}>
        <div className={clsx(stl.swiper, itemsPerPage === 1 && stl.mobSwipper)}>
          {getCurrentPageItems().map(({ id, name, title, avatar, content }) => (
            <div key={id} className={stl.testimonial}>
              <div className={stl.head}>
                <div className={stl.avatarBox}>
                  <Image
                    src={avatar}
                    alt={`${name}'s profile picture`}
                    width={38}
                    height={38}
                    style={{ borderRadius: '50%', objectFit: 'fill' }}
                  />
                </div>
                <div className={stl.info}>
                  <h4>{title}</h4>
                  <h5>{name}</h5>
                </div>
              </div>

              <div className={stl.descriptionBox}>
                <div className={stl.line} />
                <p>{content}</p>
              </div>
            </div>
          ))}
        </div>

        <Pagination
          totalItems={testimonials.length}
          itemsPerPage={itemsPerPage}
          currentPage={currentPage}
          onPageChange={setCurrentPage}
        />
      </div>
    </section>
  )
}

export default Testimonial
